  List<Articles>? articles;
  ArticlesDataModel({
      this.status,
      this.articles,});

  ArticlesDataModel.fromJson(dynamic json) {
    status = json['status'];
    if (json['articles'] != null) {
      articles = [];
      json['articles'].forEach((v) {
        articles?.add(Articles.fromJson(v));
      });
    }
  }




}

